#!/usr/bin/env python3

import sys
import subprocess
import argparse
from pathlib import Path
from .config.manager import ConfigManager
from .utils.file_manager import auto_move_csv_from_downloads

# Import version from package
from . import __version__


def clean_workspace():
    """Clean all project generated files"""
    import shutil
    
    workspace_dir = Path.cwd() / 'workspace'
    
    print("ğŸ§¹ Cleaning project generated files...")
    
    files_removed = 0
    dirs_removed = 0
    
    # Remove workspace directory and all contents
    if workspace_dir.exists():
        print(f"   ğŸ—‚ï¸  Removing workspace/ directory...")
        try:
            shutil.rmtree(workspace_dir)
            dirs_removed += 1
            print(f"   âœ… Removed workspace/ directory")
        except Exception as e:
            print(f"   âŒ Error removing workspace/: {e}")
    
    # Remove any issue report zip files in current directory
    zip_files = list(Path.cwd().glob("metis-issue-report-*.zip"))
    if zip_files:
        print(f"   ğŸ“¦ Removing {len(zip_files)} issue report zip file(s)...")
        for zip_file in zip_files:
            try:
                zip_file.unlink()
                files_removed += 1
                print(f"   âœ… Removed {zip_file.name}")
            except Exception as e:
                print(f"   âŒ Error removing {zip_file.name}: {e}")
    
    # Summary
    if files_removed > 0 or dirs_removed > 0:
        print(f"âœ… Cleanup complete!")
        print(f"   ğŸ“Š Summary: {dirs_removed} directories and {files_removed} files removed")
    else:
        print("âœ… No generated files found to clean")
    
    return True

def create_issue_report():
    """Create a zip file containing workspace subdirectories for issue reporting"""
    import zipfile
    from datetime import datetime
    
    # Create workspace directory structure if needed
    workspace_dir = Path.cwd() / 'workspace'
    if not workspace_dir.exists():
        print("âŒ No workspace directory found. Please run metis first to generate workspace content.")
        return False
    
    # Create timestamp for the zip file
    timestamp = datetime.now().strftime("%Y%m%d-%H%M%S")
    zip_filename = workspace_dir / f"metis-issue-report-{timestamp}.zip"
    
    print(f"ğŸ“¦ Creating issue report: {zip_filename}")
    
    try:
        with zipfile.ZipFile(zip_filename, 'w', zipfile.ZIP_DEFLATED) as zipf:
            # Add all files from workspace subdirectories
            subdirs = ['input', 'output', 'config', 'temp']
            total_files = 0
            
            for subdir in subdirs:
                subdir_path = workspace_dir / subdir
                if subdir_path.exists():
                    print(f"   ğŸ“ Adding {subdir}/ directory...")
                    for file_path in subdir_path.rglob('*'):
                        if file_path.is_file():
                            # Create archive path relative to workspace
                            arcname = file_path.relative_to(workspace_dir)
                            zipf.write(file_path, arcname)
                            total_files += 1
                else:
                    print(f"   âš ï¸  Skipping {subdir}/ (directory not found)")
            
            # Add the main config file if it exists
            config_file = Path.cwd() / 'metisara.conf'
            if config_file.exists():
                print(f"   ğŸ“„ Adding metisara.conf...")
                zipf.write(config_file, 'metisara.conf')
                total_files += 1
        
        print(f"âœ… Issue report created successfully!")
        print(f"   ğŸ“¦ File: {zip_filename}")
        print(f"   ğŸ“Š Contains: {total_files} files")
        print(f"   ğŸ’¡ Attach this file when reporting issues")
        return True
        
    except Exception as e:
        print(f"âŒ Error creating issue report: {e}")
        return False


